mapper.update:
UPDATE agent
SET is_active = 0
WHERE agent_id = #{_parameter}

UPDATE agent
agent_name = #{agentName}, agent_phone = #{agentPhone}, agent_email = #{agentEmail}, password = #{password}, cid_type = #{cidType}, cid_num = #{cidNum}, cid_picture = #{cidPicture},
WHERE agent_id = #{agentId}

UPDATE agent
SET login_times = login_times + 1,
    last_login_date = NOW(),
    last_login_ip = #{lastLoginIp}
WHERE agent_id = #{agentId}
